mapper.select:
/* Membermapper.xml.emailDuplicateCheckInJoinForm  이메일 개수 리턴 By email */
SELECT  COUNT(usr_id)
FROM MEMBERS
WHERE email = #{email}

/* Membermapper.xml.userNameDuplicateCheck  아이디 개수 리턴 By username*/
SELECT  COUNT(usr_id)
FROM MEMBERS
WHERE username = #{username}

/* Membermapper.xml.nickNameDuplicateCheck 닉네임 개수 리턴 By nickName */
SELECT  COUNT(usr_id)
FROM MEMBERS
WHERE nick_name = #{nickName}

 /* Membermapper.xml.FindId 아이디 찾기 */
SELECT CONCAT(SUBSTRING(username, 1, LENGTH(username) - 4), '****') AS username
  FROM MEMBERS
 WHERE 1=1
   AND email = #{email}
   AND name = #{name}

/* Membermapper.xml.findByNnAndEmail  회원정보개수 리턴 By email and username and name  */
 SELECT COUNT(usr_id)
   FROM MEMBERS
  WHERE 1=1
    AND email = #{email}
    AND name = #{name}
    AND username = #{username}

mapper.update:
/* Membermapper.xml.resetNewPassword 새 비밀번호 업데이트  */
UPDATE MEMBERS SET
       password = #{password}
 WHERE email = #{email}

mapper.select:
/* Membermapper.xml.findByUsrAndEmail  회원정보 리턴 By email and username  */
SELECT  email
      , password
      , state
   FROM MEMBERS
  WHERE 1=1
    AND email = #{email}
    AND username = #{username}

mapper.update:
/* Membermapper.xml.activationMem  계정 활성화 */
UPDATE MEMBERS SET
       state = #{state}
 WHERE email = #{email}

UPDATE MEMBERS SET
       pwd_wrong = pwd_wrong + 1
 WHERE username = #{username}

UPDATE MEMBERS SET
       state = 'LOCKED'
WHERE username = #{username}

UPDATE MEMBERS SET
       password = #{newPassword}
WHERE  usr_id   = #{usrId}
